package myproj;
import java.util.Scanner;

public class RightTrianglePattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int i,j,k,n=5;
		for(i=0;i<=n;i++) {
			k=1;
		for(j=0;j<=i;j++) {
			System.out.print(k+"");
			k++;
		}
		System.out.println();
		}

	}

}

